import React from "react";
import s from './Paginate.module.css'

const Paginate = (props) => {
    const { gamesPerPage, allGames, currentPage, paginate, prevPage, nextPage } = props;

    const pageNumbers = []

  for (let i =1; i <= Math.ceil(allGames / gamesPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
        <div >
        {currentPage !==1 && (
        <button  type='primary' style={{cursor: 'pointer'}} onClick={() => prevPage()}>{"<"}</button>)}
        {pageNumbers.map((num) => (
            <div key={num}><a style={{cursor: 'pointer'}} onClick={() => paginate(num)}>{num}</a></div>
        ))}

        {pageNumbers.length !== currentPage && (
        <button type='primary' style={{cursor: 'pointer'}} onClick={() => nextPage()}>{'>'} </button>)}
        </div>
    )
}

export default Paginate;
