import React from 'react';
import { NavLink } from 'react-router-dom';
import './Form.css';

export default function Form() {
    const [user, setUser] = React.useState ({ // Creamos estado de usuario donde se guardarán los valores de los inputs ingresados por el usuario en las siguientes propiedades:
        username: '', // declaramos el estado inicial de username y password como strings vacíos (nuestros inputs se mostrarán sin texto)
        password: ''
        });

 const [errors, setErrors] = React.useState({}) // Creamos estado de errores para ir mostrando al usuario en caso de que sus inputs no sean validos. Declaramos su estado inicial como un objeto vacío.


    return (
       <div>
       <form>
         <label htmlFor='username'>Username:</label> {/*Creamos etiqueta Username con su respectivo input de texto (declaramos su key para que React pueda diferenciarlo del otro).*/}
         <input className={errors.username && 'danger'} type='text' name='username' key='username' value={user.username} onChange={handleOnChange}></input> {/* Creamos input de tipo texto donde el usuario podrá ingresar su username(un email). Su clase solo será 'danger' si, mediante la función validadora, hay algún error en el input; esto hará que el borde del input se ponga en rojo debido al css aplicado. Su value será igual a la propiedad username de nuestro user el cual se almacenará gracias a la función handleOnChange que se ejecutará en cada evento onChange (cada vez que haya un cambio en el input.) */}
         {errors.username && (<p className='danger'>{errors.username}</p>)} {/* Si hay algún error en el input del username, es decir si errors.username es truthy (no es null), renderizamos un tag de párrafo que muestre el valor guardado en esa propiedad. En este caso la string correspondiente al error.  */}
         <label htmlFor='password'>Password:</label> {/*Creamos etiqueta Password con su respectivo input de texto (declaramos su key para que React pueda diferenciarlo del otro).*/}
         <input className={errors.password && 'danger'} type='password' name='password' key='password' value={user.password} onChange={handleOnChange}></input> {/* Creamos input de tipo texto donde el usuario podrá ingresar su password. Su clase solo será 'danger' si, mediante la función validadora, hay algún error en el input; esto hará que el borde del input se ponga en rojo debido al css aplicado. Su value será igual a la propiedad password de nuestro user el cual se almacenará gracias a la función handleOnChange que se ejecutará en cada evento onChange (cada vez que haya un cambio en el input.) */}
         {errors.password && <p className='danger'>{errors.password}</p>} {/* Si hay algún error en el input del password, es decir si errors.password es truthy (no es null), renderizamos un tag de párrafo que muestre el valor guardado en esa propiedad. En este caso la string correspondiente al error.  */}
         <input type='submit' value='Submit'></input> {/*Creamos input de tipo submit para poder submitear el form. */}
       </form> 
      </div>
    )
}
