import React, { Component, useEffect, useState } from 'react';
import { NavLink, Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { getAllVideogames } from '../../actions';
import h from './Home.module.css';
import Post from '../Post/Post';


export const Home = () => {
    // constructor(props){
    //     super(props)
    // }
   const [posts, setPosts] = useState([])
   const [loading, setLoading] = useState(false)
   const [currentPage, setCurrentPage] = useState(1);
   const [postPerPage] = useState(15);
   const [totalPosts, setTotalPosts] = useState(0);

   useEffect(() => {
     const loadPost = async () => {
        setLoading(true)
        await this.props.getAllVideogames()
        setPosts(this.props.videogames)
        setTotalPosts(this.props.videogames.length)
        setLoading(false)
     }
    loadPost()
   }, [])

   const indexOfLastPost = currentPage + postPerPage;
   const indexOfFirstPost = indexOfLastPost - postPerPage;
   const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost)
   const paginate = (pageNumber) => setCurrentPage(pageNumber);
   const prevPage= () => setCurrentPage(currentPage - 1);
    const nextPage= () => setCurrentPage(currentPage + 1);
    const showPagination = () => {
        return ( 
            <Post
            postPerPage={postPerPage}
            totalPosts={totalPosts}
            currentPage={currentPage}
            paginate={paginate}
            prevPage={prevPage}
            nextPage={nextPage}
            />
        )
        }

    return (
        <header className="videogames">

             <div className='createVd'>
                <Link to='/createVideoGame'><button>Create Game</button></Link>
            </div>

             {/* <div className={h.vgContainer}>
                    ACA VAN LOS JUEGUITOS 游뗵
                    <ul>
                        {this.props.videogames && this.props.videogames.map(vg => 
                            <div key={vg.id}>
                                <img className={h.vgImage} src={vg.background_image} alt='videogameImage'/>
                                <h3><Link to={`/videogames/${vg.id}`}>{vg.name}</Link></h3>
                                <li>Platforms: {vg.platforms.join(' | ')}</li>
                                <li>Genres:  {vg.genres.map(g => g.name).join(' | ')}</li>
                            </div>)
                        }
                    </ul>
             </div> */}

                    <div className={h.vgContainer}>
                    ACA VAN LOS JUEGUITOS 游뗵
                    <ul>
                        {!loading ? currentPosts.map(vg => (
                            <div key={vg.id}>
                                <img className={h.vgImage} src={vg.background_image} alt='videogameImage'/>
                                <h3><Link to={`/videogames/${vg.id}`}>{vg.name}</Link></h3>
                                <li>Platforms: {vg.platforms.join(' | ')}</li>
                                <li>Genres:  {vg.genres.map(g => g.name).join(' | ')}</li>
                            </div>)) : <h1>Loading...</h1>
                            }
                    </ul>
                    <div>{showPagination()}</div>
             </div>

        </header>
    )

}

function mapStateToProps(state) { // Creamos funci칩n mapStateToProps que nos traer치 el state global almacenado en store.
    return {
        videogames: state.videogames // // Creamos propiedad movie donde nos guardaremos el objeto de la pel칤cula seleccionada que guardar치 como propiedades sus detalles. Ahora al componente Movie, le llegar치 como props la propiedad movie. (podr치 acceder mediante this.movie)
    }
}

function mapDispatchToProps(dispatch) {
    return {
        getAllVideogames: () => dispatch(getAllVideogames()) 
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Home); // Conectamos a nuestro componente en primer lugar con el estado global (store) pas치ndolo como prop mediante mapStateToProps, y en segundo lugar con las actions mediante mapDispatchToProps.
