import React, { useEffect, useState } from 'react';
import { NavLink, Link } from 'react-router-dom';
import {  useDispatch, useSelector } from 'react-redux';
import { getAllVideogames, getVideogamesByGenre } from '../../actions';
import h from './Home.module.css';
import Paginate from '../Paginate/Paginate';


const Home = () => {
    // constructor(props){
    //     super(props)
    // }
    const dispatch = useDispatch();
    const allGames = useSelector(state => state.videogames);
    const allGenres = useSelector(state => state.genres);
    const [currentPage, setCurrentPage] = useState(1);
    const [gamesPerPage] = useState(15);
    const indexOfLastGame = currentPage * gamesPerPage;
    const indexOfFirstGame = indexOfLastGame - gamesPerPage;
    const currentGames = allGames.slice(indexOfFirstGame, indexOfLastGame)

   const [loading, setLoading] = useState(false)

   useEffect(() => {
       console.log(loading)
       dispatch(getAllVideogames())
       setLoading(true)
        console.log(loading)
   }, [])

   const paginate = (pageNumber) => setCurrentPage(pageNumber);
   const prevPage= () => setCurrentPage(currentPage - 1);
    const nextPage= () => setCurrentPage(currentPage + 1);
    const showPagination = () => {
        return ( 
            <Paginate
            gamesPerPage={gamesPerPage}
            allGames={allGames.length}
            currentPage={currentPage}
            paginate={paginate}
            prevPage={prevPage}
            nextPage={nextPage}
            />
        )
        }

    const handleOnChangeFilterGenres = (e) => {
      dispatch(getVideogamesByGenre(e.target.value))
    }


    return (
        <header className="videogames">

             <div className='createVd'>
                <Link to='/createVideoGame'><button>Create Game</button></Link>
            </div>


            <div className='filters'>
                <div className='genre_games_filters'>
                        <select name='all_db_games' id='games' size='2'>
                            <option value='all_games'>All Games</option>
                            <option value='db_games'>Created Games</option>
                        </select>
                        <select name='genres' id='genres' size='20' onChange={(e) => handleOnChangeFilterGenres(e)}>
                            <optgroup label='Genres'>
                               {allGenres.map(g =>
                               <option value={g.id} key={g.id}>{g.name}</option> )}
                            </optgroup>
                         </select>
                </div>
                <div className='alphabetic_rating_orders'>
                      <label>Order By</label>
                        <select name='alphabetic' size='6'>
                            <optgroup label='Alphabetic'>
                                <option value='a-z'>A-Z</option>
                                <option value='z-a'>Z-A</option>
                            </optgroup>
                         <br></br>
                            <optgroup label='Rating'>
                                <option value='1-5'>1-5</option>
                                <option value='5-1'>5-1</option>
                        </optgroup>
                         </select>
                </div>
             </div>

                    <div className={h.vgContainer}>
                    ACA VAN LOS JUEGUITOS ðŸ™‚
                    <ul>
                        {loading ? currentGames.map(vg => (
                            <div key={vg.id}>
                                <img className={h.vgImage} src={vg.background_image} alt='videogameImage'/>
                                <h3><Link to={`/videogames/${vg.id}`}>{vg.name}</Link></h3>
                                <li>Platforms: {vg.platforms.join(' | ')}</li>
                                <li>Genres:  {vg.genres.map(g => g.name).join(' | ')}</li>
                            </div>)) : <h1>Loading...</h1>
                            }
                    </ul>
                    <div>{showPagination()}</div>
             </div>

        </header>
    )

}

export default Home;
