import React from 'react';
import { NavLink } from 'react-router-dom';
import NavBar from '../NavBar/NavBar';
import profile from '../Icons/profile.jpg';
import s from './FAQ.module.css';


export default function About() {
    return (
        <div>
            <NavBar/>       
            <div className={s.container}>
                <div>
                <h2>What is Ki Games?</h2>
                <p>Ki Games is a prototype of a leading videogame database platform where we combine not only the most popular companies games of the world but also we want to give you the opportunity to be a part of this universe and promove your own videogames.</p>
                <h2>What can I do here?</h2>
                <p>You can search video games by name, filter them by more than one genre and platform, and combine them with each other.</p> 
                <h2>And what if I sign up?</h2>
                <p>You can show your talent to the world and upload your own videogames to be ranked by the community and have the opportunity to become popular.</p> 
                <h2>How can I contribute to Ki Games?</h2>
                <p>Just contact me and give me your feedback about the site! </p> 
                </div>

                <div className={s.about}>
                <img  src={profile} alt='profile'></img>
                <h2>About me</h2>
                <p>Hi everybody! My name is Joaqu√≠n Gioffre and welcome to my first project.</p> 
                </div>
            </div>
        </div>
    )
}
