import React, { Component } from 'react';
import { NavLink, Link } from 'react-router-dom';
import {connect} from 'react-redux';
import { getVideogameDetail, deleteVideogame} from '../../actions/index';
import NavBar from '../NavBar/NavBar';
import s from './VideogameDetail.module.css'
import rating from '../Icons/rating.png'
import swal from 'sweetalert';


export class VideogameDetail extends Component {

    componentDidMount() { // Invocamos la funci칩n componentDidMount para que cada vez que el componente sea invocado...
        const videogameDetail = this.props.match.params.videogameId
        this.props.getVideogameDetail(videogameDetail)
    }

    vg = this.props.vgDetail

    handleOnDelete = () => {
        console.log(this.props.vgDetail[0].id)
        this.props.deleteVideogame(this.props.vgDetail[0].id)
        this.props.history.push('/home')

        // swal({
        //     title: "Are you sure?",
        //     text: "Once deleted, you will not be able to recover this videogame!",
        //     icon: "warning",
        //     buttons: true,
        //     dangerMode: true,
        //   })
        //   .then((willDelete) => {
        //     if (willDelete) {
        //      this.props.deleteVideogame(this.props.vgDetail.id)
        //      this.props.history.push('/home')
        //       swal("Videogame has been deleted!", {
        //         icon: "success",
        //       });
        //     } else {
        //       swal("Your videogame is safe!");
        //     }
        //   });
    }

    handleOnClick = () => {
        this.props.history.push('/home')
    }

    render(){
    return (  
        <div>
        <NavBar/>
        <button onClick={this.handleOnClick}>Return</button>
        <br></br>
     {this.vg &&
        <div>
        <div key={this.vg.id} className={s.container}>
            <div><img className={s.image} src ={this.vg.background_image} alt='imageVgD'/></div>
            <div className={s.vgContainer}>
                 <h2>{this.vg.name}</h2>
                 <div>
                    <h3>Rating: {this.vg.rating}</h3> <img className={s.imageP} src={rating}></img> 
                    <h3>Released: {this.vg.released}</h3>
                 </div>
                <div>Platforms: {this.vg.platforms.join(' | ')}</div>
                <div>Genres: {this.vg.genres.map(g => g.name).join(' | ')}</div>
                {this.vg.createdOnDb && <button onClick={this.handleOnDelete}>DELETE GAME</button>}
            </div>
        </div>
        <div className={s.description}>
            <p >{this.vg.description}</p>
        </div>
        </div>
    }
    </div>
    )
}
}


function mapStateToProps(state) { 
    return {
        vgDetail: state.videogameDetail,
    }
}

function mapDispatchToProps(dispatch) { // Creamos funci칩n para pasar por props las actions a nuestro componente Movie.
    return {
        getVideogameDetail: videogameId => dispatch(getVideogameDetail(videogameId)),
        deleteVideogame: id => dispatch(deleteVideogame(id)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(VideogameDetail); // Conectamos a nuestro componente en primer lugar con el estado global (store) pas치ndolo como prop mediante mapStateToProps, y en segundo lugar con las actions mediante mapDispatchToProps.
