import React from "react";
import s from "./ModalDelete.module.css";
import mario from '../Icons/mario.png';
import { deleteVideogame, getAllVideogames } from '../../actions';
import {  useDispatch } from 'react-redux';
import sonic from '../Icons/sonic.png';





// Modal.setAppElement("#root");

export default function ModalDelete(createdOnDb, vgId) {

   const [show, setShow] = React.useState(false);
   const [success, setSuccess] = React.useState(false);

   const dispatch = useDispatch();


  const handleOnCancel = () => {
       setShow(false);
  }

  const handleOnDelete = (vgId) => {
    setShow(false);
    // dispatch(deleteVideogame(vgId))
    // dispatch(getAllVideogames())
    setSuccess(true);

}

 const handleOnClick = () => {
    setShow(true)
 }


  return (
    <div>

        <div className={show ? s.mymodal : s.notShow}>

            <div className={s.content}>
                    <div>
                        <h2>Are you sure you want to delete this videogame?</h2>
                        <p>You will not be able to get it back a</p>
                        <button onClick={() => handleOnCancel()}>CANCEL</button>
                        <button onClick={(vgId) => handleOnDelete(vgId)}>DELETE</button>
                    </div>
                    <div><img className={s.mario} src={mario} alt='mario'></img> </div>
            </div>  
        </div>
        {createdOnDb && <div className={s.deleteGame}><button onClick={() => handleOnClick()}>DELETE GAME</button></div>}


        <div className={success ? s.success : notShow}>
                <img className={s.sonic} src={sonic} alt='mario'></img> 
        </div>
    </div>
  );
}
