import { GET_VIDEOGAME_DETAIL, GET_SEARCH_VIDEOGAMES, DELETE_VIDEOGAME, CREATE_VIDEOGAME, GET_ALL_VIDEOGAMES, GET_GENRES, GET_FILTERS} from '../actions/index';


const initialState = {
    filteredVideogames: [],
    videogames: [],
    videogameDetail:[],
    genres: []
}

export default function rootReducer(state = initialState, action) {

    switch(action.type) {
        case GET_ALL_VIDEOGAMES: return {...state, videogames: action.payload, filteredVideogames:action.payload}

        case GET_SEARCH_VIDEOGAMES: return {...state, videogames: action.payload}

        case GET_VIDEOGAME_DETAIL: return {...state, videogameDetail: [action.payload]}

        case CREATE_VIDEOGAME: return {...state}

        case DELETE_VIDEOGAME: return {...state}

        case GET_GENRES: return {...state, genres: action.payload}

        /*
        GET_VIDEOGAMES_BY_GENRE tiene que filtrar mi actions.payload (videogames) donde alguna propiedad name de los objetos dentro de su propiedad genre (que es un array de genres), coincida con el genre se le pasa como argumento a la action.
        */

        case GET_FILTERS: console.log(action.payload); 
        let filters = [...state.videogames]

        if(action.payload.filterByGenre !== 'all') filters = filters.map(videogame => videogame.genres.filter(genre => genre.name == action.payload.filterByGenre))
        // == videogame.genres.includes(genre => genre.name == action.payload.filterByGenre))


        console.log(filters)

        return {...state, filteredVideogames: filters}

        // return {...state, filteredVideogames:};

        default: return {...state}

    }
}
