import React, { Component, useEffect, useState, useDispatch, useSelector } from 'react';
import { NavLink, Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { getAllVideogames } from '../../actions';
import h from './Home.module.css';
import Paginate from '../Paginate/Paginate';


const Home = () => {
    // constructor(props){
    //     super(props)
    // }
    const dispatch = useDispatch();
    const allGames = useSelector(state => state.videogames);
    const [currentPage, setCurrentPage] = useState(1);
    const [gamesPerPage] = useState(15);
    const indexOfLastGame = currentPage * gamesPerPage;
    const indexOfFirstGame = indexOfLastGame - gamesPerPage;
    const currentGames = allGames.slice(indexOfFirstGame, indexOfLastGame)

   const [loading, setLoading] = useState(false)
   const [totalGames, setTotalGames] = useState(0);

   useEffect(() => {
     const loadPost = async () => {
        setLoading(true)
        dispatch(getAllVideogames())
        setTotalGames(this.videogames.length)
        setLoading(false)
     }
    loadPost()
   }, [])

   const paginate = (pageNumber) => setCurrentPage(pageNumber);
   const prevPage= () => setCurrentPage(currentPage - 1);
    const nextPage= () => setCurrentPage(currentPage + 1);
    const showPagination = () => {
        return ( 
            <Paginate
            gamesPerPage={gamesPerPage}
            totalGames={totalGames}
            currentPage={currentPage}
            paginate={paginate}
            prevPage={prevPage}
            nextPage={nextPage}
            />
        )
        }

    return (
        <header className="videogames">

             <div className='createVd'>
                <Link to='/createVideoGame'><button>Create Game</button></Link>
            </div>

             {/* <div className={h.vgContainer}>
                    ACA VAN LOS JUEGUITOS 游뗵
